id, code, name, is_system, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ code }, #{ name }, #{ isSystem }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE master_data_type SET
code = #{ code }, name = #{ name }, is_system = #{ isSystem }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE master_data_type SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO master_data_type(
