mapper.insert:
INSERT INTO standard_field(
id, master_data_type_id, data_key, filed_code, filed_name, filed_type, filed_size, filed_precision, required, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ masterDataTypeId }, #{ dataKey }, #{ filedCode }, #{ filedName }, #{ filedType }, #{ filedSize }, #{ filedPrecision }, #{ required }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE standard_field SET
master_data_type_id = #{ masterDataTypeId }, data_key = #{ dataKey }, filed_code = #{ filedCode }, filed_name = #{ filedName }, filed_type = #{ filedType }, filed_size = #{ filedSize }, filed_precision = #{ filedPrecision }, required = #{ required }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE standard_field SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

