mapper.select:
SELECT

FROM sys_db_type a

WHERE a.id = #{id}

SELECT

FROM sys_db_type a


  1=1
  AND a.id > 0
  AND a.del_flag = #{DEL_FLAG_NORMAL}




    ORDER BY ${order}


    ORDER BY a.update_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT
FROM sys_db_type a
1=1 AND a.id > 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO sys_db_type(
id, name, driver, rule, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ name }, #{ driver }, #{ rule }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE sys_db_type SET
name = #{ name }, driver = #{ driver }, rule = #{ rule }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE sys_db_type SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO sys_db_type(
id, name, driver, rule, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.name}, #{entity.driver}, #{entity.rule}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE sys_db_type



      when id=#{entity.id} then #{entity.name}




      when id=#{entity.id} then #{entity.driver}




      when id=#{entity.id} then #{entity.rule}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}

UPDATE sys_db_type



      when id=#{entity.id} then 1




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}
