mapper.select:
SELECT a.template_json AS "templateJson", IFNULL(template.json,'') AS "template.json",
FROM dc_clien_scheme a
WHERE a.id = #{id}

SELECT

FROM dc_clien_scheme a


  1=1
  AND a.id > 0
  AND a.del_flag = #{DEL_FLAG_NORMAL}




    ORDER BY ${order}


    ORDER BY a.update_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT
FROM dc_clien_scheme a
1=1 AND a.id > 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO dc_clien_scheme(
id, name, scheme_id, template_id, template_json, remarks, create_by, create_date, update_by, update_date, del_flag, project_id,
) VALUES (
#{ id }, #{ name }, #{ scheme.id }, #{ template.id }, #{ templateJson }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag }, #{ project.id },
)

mapper.update:
UPDATE dc_clien_scheme SET
name = #{ name }, scheme_id = #{ scheme.id }, template_id = #{ template.id }, template_json = #{ templateJson }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate }, project_id = #{ project.id },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE dc_clien_scheme SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

