mapper.select:
SELECT a.template_json AS "templateJson", IFNULL(template.json,'') AS "template.json",
FROM dc_mobile_scheme a
WHERE a.id = #{id}

SELECT

FROM dc_mobile_scheme a


  1=1
  AND a.id > 0
  AND a.del_flag = #{DEL_FLAG_NORMAL}




    ORDER BY ${order}


    ORDER BY a.update_date DESC


LIMIT #{limit} OFFSET #{offset}

SELECT
FROM dc_mobile_scheme a
1=1 AND a.id > 0 AND a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY ${order}
ORDER BY a.update_date DESC

mapper.insert:
INSERT INTO dc_mobile_scheme(
id, project_id, name, scheme_id, template_id, template_json, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ project.id }, #{ name }, #{ scheme.id }, #{ template.id }, #{ templateJson }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE dc_mobile_scheme SET
project_id = #{ project.id }, name = #{ name }, scheme_id = #{ scheme.id }, template_id = #{ template.id }, template_json = #{ templateJson }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE dc_mobile_scheme SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO dc_mobile_scheme(
