mapper.select:
        SELECT 

        FROM information_schema.tables a


            a.TABLE_SCHEMA = (select database())




                ORDER BY ${order}


	    	ORDER BY name ASC


        LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO information_schema.tables(
    table_name,
    table_comment,
    table_type
) VALUES (
    #{name},
    #{comments},
    #{source}
)

mapper.update:
UPDATE information_schema.tables SET     
    table_name = #{name},
    table_comment = #{comments},
    table_type = #{source}
WHERE id = #{id}
