mapper.insert:
INSERT INTO micro_app(
id, developer_id, name, full_name, is_locked, remarks, create_by, create_date, update_by, update_date, del_flag, icon_id, app_key,
) VALUES (
#{ id }, #{ developer.id }, #{ name }, #{ fullName }, #{ isLocked }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag }, #{ iconId }, #{ appKey },
)

mapper.update:
UPDATE micro_app SET
developer_id = #{ developer.id }, name = #{ name }, full_name = #{ fullName }, is_locked = #{ isLocked }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate }, icon_id = #{ iconId }, app_key = #{ appKey },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE micro_app SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

