mapper.insert:
INSERT INTO prj_member(
id, project_id, user_id, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES (
#{ id }, #{ project.id }, #{ user.id }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag },
)

mapper.update:
UPDATE prj_member SET
project_id = #{ project.id }, user_id = #{ user.id }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE prj_member SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

mapper.insert:
INSERT INTO prj_member(
id, project_id, user_id, remarks, create_by, create_date, update_by, update_date, del_flag,
) VALUES
(
#{entity.id}, #{entity.project.id}, #{entity.user.id}, #{entity.remarks}, #{entity.createBy}, #{entity.createDate}, #{entity.updateBy}, #{entity.updateDate}, #{entity.delFlag},
)

mapper.update:
UPDATE prj_member



      when id=#{entity.id} then #{entity.project.id}




      when id=#{entity.id} then #{entity.user.id}




      when id=#{entity.id} then #{entity.remarks}




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



WHERE

  id = #{entity.id} and update_date = #{entity.oldUpdateDate}

UPDATE prj_member



      when id=#{entity.id} then 1




      when id=#{entity.id} then #{entity.updateBy}




      when id=#{entity.id} then #{entity.updateDate}



